import React, {useEffect} from "react";

export function BookItProjectPage(){

    const openTab=(event, tabName)=>{
    // Get all elements with class="tabcontent" and hide them
    let tabcontent = document.getElementsByClassName("tabcontent");
    for (let i = 0; i < tabcontent.length; i++) {
        tabcontent[i].style.display = "none";
    }

    // Get all elements with class="tablinks" and remove the class "active"
    let tablinks = document.getElementsByClassName("tablinks");
    for (let i = 0; i < tablinks.length; i++) {
      tablinks[i].className = tablinks[i].className.replace(" active", "");
    }

    // Show the current tab, and add an "active" class to the link that opened the tab
    let thisDocument = document.getElementById(tabName)
    thisDocument.style.display = "block";
    event.currentTarget.className += " active";
    }

    useEffect(()=>{
        document.getElementById("defaultOpen").click()
    },[])
    return(
        <article>
            <div class="tab">
                <button class="tablinks" id="defaultOpen" onClick={(event)=>openTab( event,"Summary")}> Content</button>
                <button class="tablinks"  onClick={(event)=>openTab(event, "ReadingHistory")}>Reading History</button>
                {/* <button class="tablinks" onClick={()=>openTab(event, "Backlog")}>Backlog</button> */}

            </div>
            <section>
                <h1>Byte</h1>
                <br></br>
                <div id="Summary" class="tabcontent">
                <ul>
                    <li>2022</li>
                    <li>In Progress</li>
                </ul>
                    <p>
                        Byte was designed to solve two time consuming problems. First, to quickly digitizing recipes from cookbooks instead of manually typing them into an cooking application.
                        Second, to calculate nutrition info from a recipe's listed ingredients. 
                    </p>
                </div>
                <div id="ReadingHistory" class="tabcontent">
                    <h2>Your Reading History</h2>
                    <hr/>
                </div>
                <div id="Backlog" class="tabcontent">
                    <h2>Your Backlog</h2>
                    <hr/>
                        <div id="backlog" className="tab-content-row">
                        </div>
                </div>
            </section>
        </article>
)}
